import React, { useState } from 'react';

function Preferences() {
  const [preferences, setPreferences] = useState({
    diet: [], // Change diet to an array to hold multiple selected values
    allergies: '',
    bannedIngredients: '',
  });

  const handleDietChange = (e) => {
    const options = e.target.options;
    const selectedDiets = [];
    for (const option of options) {
      if (option.selected) {
        selectedDiets.push(option.value);
      }
    }
    setPreferences({
      ...preferences,
      diet: selectedDiets, // Update diet with the selected values
    });
  };

  const handleChange = (e) => {
    setPreferences({
      ...preferences,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Preferences submitted:', preferences);
  };

  return (
    <div className="page">
      <h2>Set Your Preferences</h2>
      <form onSubmit={handleSubmit} className="preferences-form">
        <label>
          Diet:
          <select
            name="diet"
            multiple // Enable multiple selection
            value={preferences.diet} // Bind to the array in state
            onChange={handleDietChange}
          >
            <option value="none">None</option>
            <option value="vegetarian">Vegetarian</option>
            <option value="lacto-vegetarian">Lacto-Vegetarian</option>
            <option value="ovo-vegetarian">Ovo-Vegetarian</option>
            <option value="vegan">Vegan</option>
            <option value="ketogenic">Keto</option>
            <option value="gluten free">Gluten Free</option>
            <option value="pescetarian">Pescetarian</option>
            <option value="paleo">Paleo</option>
            <option value="primal">Primal</option>
            <option value="low fodmap">Low FODMAP</option>
            <option value="whole30">Whole30</option>
          </select>
        </label>
        <label>
          Allergies:
          <input
            type="text"
            name="allergies"
            value={preferences.allergies}
            onChange={handleChange}
            placeholder="E.g., peanuts, shellfish"
          />
        </label>
        <label>
          Banned Ingredients:
          <input
            type="text"
            name="bannedIngredients"
            value={preferences.bannedIngredients}
            onChange={handleChange}
            placeholder="E.g., pork, gluten"
          />
        </label>
        <button type="submit">Save Preferences</button>
      </form>
    </div>
  );
}

export default Preferences;
