import React, { useState } from "react";

function Preferences() {
  const [preferences, setPreferences] = useState({
    diet: [], // Array to hold selected diets
    allergies: "",
    bannedIngredients: "",
  });

  const dietOptions = [
    "None",
    "Vegetarian",
    "Lacto-Vegetarian",
    "Ovo-Vegetarian",
    "Vegan",
    "Ketogenic",
    "Gluten Free",
    "Pescetarian",
    "Paleo",
    "Primal",
    "Low FODMAP",
    "Whole30",
  ];

  const handleDietChange = (diet) => {
    setPreferences((prev) => {
      const newDiet = prev.diet.includes(diet)
        ? prev.diet.filter((item) => item !== diet) // Remove if already selected
        : [...prev.diet, diet]; // Add if not selected
      return { ...prev, diet: newDiet };
    });
  };

  const handleChange = (e) => {
    setPreferences({
      ...preferences,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Preferences submitted:", preferences);
  };

  return (
    <div className="page">
      <h2>Set Your Preferences</h2>
      <form onSubmit={handleSubmit} className="preferences-form">
        <fieldset>
          <legend>Diet:</legend>
          {dietOptions.map((option) => (
            <label key={option}>
              <input
                type="checkbox"
                name="diet"
                value={option}
                checked={preferences.diet.includes(option)}
                onChange={() => handleDietChange(option)}
              />
              {option}
            </label>
          ))}
        </fieldset>
        <label>
          Allergies:
          <input
            type="text"
            name="allergies"
            value={preferences.allergies}
            onChange={handleChange}
            placeholder="E.g., peanuts, shellfish"
          />
        </label>
        <label>
          Banned Ingredients:
          <input
            type="text"
            name="bannedIngredients"
            value={preferences.bannedIngredients}
            onChange={handleChange}
            placeholder="E.g., pork, gluten"
          />
        </label>
        <button type="submit">Save Preferences</button>
      </form>
    </div>
  );
}

export default Preferences;
